import React, { useState, useEffect } from "react";
import axios from "axios";
import style from "../style/body.module.css";





const MainBody = () => {
  const url = "https://dummyjson.com/products";
  const [products, setProducts] = useState([]);

  useEffect(() => {
    handleProducts();
  }, []);

  const handleProducts = async () => {
    try {
      const { data } = await axios.get(url);
      setProducts(data.products);
      console.log(data.products);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center' }}>
  <div style={{ width: '80%', maxWidth: '800px' }}>
    <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>Products</h1>
    <ul style={{ listStyle: 'none', display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' }}>
      {products.map((item) => (
        <li key={item.id} style={{ border: '1px solid #ddd', padding: '10px', textAlign: 'center' }}>
          <img src={item.images[0]} alt="product" style={{ maxWidth: '100%', height: 'auto', marginBottom: '10px' }} />
          <p style={{ fontSize: '18px', marginBottom: '5px' }}>{item.title}</p>
          <span style={{ fontSize: '16px', fontWeight: 'bold' }}>{item.price}$</span>
        </li>
      ))}
    </ul>
  </div>
</div>

  );
};

export default MainBody;
