import React, { useEffect, useState } from "react";
import {
  List,
  ListItem,
  ListItemButton,
  ListItemAvatar,
  ListItemText,
  Checkbox,
  Input,
  Container,
  Avatar,
  Box,
  Button,
  Menu,
  MenuItem,
} from "@mui/material";
import "../index.css";
import axios from "axios";
import { Dropdown } from "bootstrap";

const ShoppingList = ({ sendProduct, setsendProduct }) => {
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [images, setImages] = useState("");
  let [products, setProducts] = useState([]);
  const token = localStorage.getItem("token");

  const showProducts = async () => {
    try {
      const userData = await axios.get("http://localhost:3001/users/myInfo", {
        headers: {
          "x-api-key": token,
        },
      });

      const { data } = await axios.get("http://localhost:3001/product");
      const userProducts = data.filter(
        (product) => product.user_id === userData.data._id
      );
      setProducts(userProducts);
    } catch (error) {
      console.log(error);
    }
  };

  const handleValues = async (e) => {
    e.preventDefault();
    const newProduct = {
      title: title,
      price: price,
      images: [images],
    };
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/product",
        newProduct,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      const data = response.data;
      setProducts([...products, data]);
      console.log([...products, data]);
    } catch (error) {
      console.log(error);
    }
    setsendProduct([...sendProduct, newProduct]);
    console.log([...sendProduct, newProduct]);
    setTitle("");
    setPrice("");
    setImages("");
  };

  const deleteProduct = async (product) => {
    try {
      console.log(product);
      const { data } = await axios.delete(
        `http://localhost:3001/product/${product._id}`,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const editProduct = async (product) => {
    try {
      console.log(product);
      const { data } = await axios.put(
        `http://localhost:3001/product/${product._id}`,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    showProducts();
  }, []);

  return (
    <div>
      <Container>
        <form onSubmit={handleValues}>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            marginTop={2}
          >
            <Input
              placeholder="title of the product"
              sx={{ margin: 2 }}
              onChange={(e) => setTitle(e.target.value)}
              name="title"
              required
            />
            <Input
              placeholder="price"
              sx={{ margin: 2 }}
              onChange={(e) => setPrice(e.target.value)}
              name="price"
              required
            />
            <Input
              placeholder="images (url)"
              sx={{ margin: 2 }}
              onChange={(e) => setImages(e.target.value)}
              name="img_url"
              required
            />
            <Button type="submit">submit</Button>
          </Box>
        </form>
        <List
          dense
          sx={{
            width: "100%",
            maxWidth: 360,
            bgcolor: "background.paper",
            marginTop: 2,
          }}
        >
          {products.map((product, index) => {
            const labelId = `checkbox-list-secondary-label-${index}`;
            return (
              <ListItem key={index} disablePadding>
                <ListItemButton>
                  <ListItemAvatar>
                    <Avatar
                      alt={product.description}
                      src={product.images && product.images[0]}
                      sx={{ width: 50, height: 50 }}
                    />
                  </ListItemAvatar>
                  <ListItemText
                    id={labelId}
                    primary={`${product.price}$`}
                    secondary={product.title}
                  />
                </ListItemButton>
                <Dropdown>
                  <Dropdown.Toggle variant="success" id="dropdown-basic">
                  <i class="fa-solid fa-bars"></i>
                  </Dropdown.Toggle>

                  <Dropdown.Menu>
                    <Dropdown.Item>Action</Dropdown.Item>
                    <Dropdown.Item>
                      Another action
                    </Dropdown.Item>
                    <Dropdown.Item href="#/action-3">
                      Something else
                    </Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
                <button
                  className="delete_product"
                  onClick={() => deleteProduct(product)}
                >
                  delete
                </button>
                <button
                  className="edit_product"
                  onClick={() => editProduct(product)}
                >
                  edit
                </button>
              </ListItem>
            );
          })}
        </List>
      </Container>
    </div>
  );
};

export default ShoppingList;
