import React, { useState } from "react";
import "../style/login.css";

const Login = () => {
  const [currentStep, setCurrentStep] = useState(1);

  const nextStep = () => {
    setCurrentStep(currentStep + 1);
  };

  const prevStep = () => {
    setCurrentStep(currentStep - 1);
  };

  const handleSubmit = () => {
    alert("Your Form Successfully Signed up");
    // Optionally, you can also use React Router to navigate to a different page
  };

  return (
    <div className="container">
      {/* ... */}
      <div className="form-outer">
        <form action="#">
          <div className={`page slide-page page-${currentStep}`}>
            {/* ... */}
            <div className="field btns">
              {currentStep > 1 && (
                <button className={`prev-${currentStep} prev`} onClick={prevStep}>
                  Previous
                </button>
              )}
              {currentStep < 4 ? (
                <button className={`next-${currentStep} next`} onClick={nextStep}>
                  Next
                </button>
              ) : (
                <button className="submit" onClick={handleSubmit}>
                  Submit
                </button>
              )}
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
