import React, { useEffect, useState } from "react";
import {
  List,
  ListItem,
  ListItemButton,
  ListItemAvatar,
  ListItemText,
  Checkbox,
  Input,
  Container,
  Avatar,
  Box,
  Button,
} from "@mui/material";
import "../index.css";
import axios from "axios";

import { ContextMenuItem, useContextMenu } from "use-context-menu";
import "use-context-menu/styles.css";

const ShoppingList = ({ sendProduct, setsendProduct } , { className }: { className: string }) => {
  const [checked, setChecked] = useState([]);
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [images, setImages] = useState("");
  let [products, setProducts] = useState([]);

  const showProducts = async () => {
    try {
      const { data } = await axios.get("http://localhost:3001/product");
      setProducts(data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleValues = async (e) => {
    e.preventDefault();
    const newProduct = {
      title: title,
      price: price,
      images: [images],
    };
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/product",
        newProduct,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      const data = response.data;
      setProducts([...products, data]);
      console.log([...products, data]);
    } catch (error) {
      console.log(error);
    }
    setsendProduct([...sendProduct, newProduct]);
    console.log([...sendProduct, newProduct]);
    setTitle("");
    setPrice("");
    setImages("");
  };

  const deleteProduct = async () => {};

  const editProduct = async () => {};

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  const { contextMenu, onContextMenu, onKeyDown } = useContextMenu(
    <>
      <ContextMenuItem onSelect={deleteProduct}>One</ContextMenuItem>
      <ContextMenuItem onSelect={editProduct}>Two</ContextMenuItem>
    </>
  );

  useEffect(() => {
    showProducts();
  }, []);

  return (
    <div>
      <Container>
        <form onSubmit={handleValues}>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            marginTop={2}
          >
            <Input
              placeholder="title of the product"
              sx={{ margin: 2 }}
              onChange={(e) => setTitle(e.target.value)}
              name="title"
              required
            />
            <Input
              placeholder="price"
              sx={{ margin: 2 }}
              onChange={(e) => setPrice(e.target.value)}
              name="price"
              required
            />
            <Input
              placeholder="images (url)"
              sx={{ margin: 2 }}
              onChange={(e) => setImages(e.target.value)}
              name="img_url"
              required
            />
            <Button type="submit">submit</Button>
          </Box>
        </form>
        <List
          dense
          sx={{
            width: "100%",
            maxWidth: 360,
            bgcolor: "background.paper",
            marginTop: 2,
          }}
        >
          {products.map((product, index) => {
            const labelId = `checkbox-list-secondary-label-${index}`;
            return (
              <ListItem
                key={index}
                secondaryAction={
                  // <Checkbox
                  //   edge="end"
                  //   onChange={handleToggle(index)}
                  //   checked={checked.indexOf(index) !== -1}
                  //   inputProps={{ "aria-labelledby": labelId }}
                  // />
                  <>
                    <button
                      onContextMenu={onContextMenu}
                      onKeyDown={onKeyDown}
                      tabIndex={0}
                    >
                      <i class="fa-solid fa-bars"></i>
                    </button>
                    {contextMenu}
                  </>
                }
                disablePadding
              >
                <ListItemButton>
                  <ListItemAvatar>
                    <Avatar
                      alt={product.description}
                      src={product.images && product.images[0]}
                      sx={{ width: 50, height: 50 }}
                    />
                  </ListItemAvatar>
                  <ListItemText
                    id={labelId}
                    primary={`${product.price}$`}
                    secondary={product.title}
                  />
                </ListItemButton>
              </ListItem>
            );
          })}
        </List>
      </Container>
    </div>
  );
};

export default ShoppingList;
