import React from "react";
import "../style/login.css";
import { Link } from "react-router-dom";
import { useState } from "react";

const Login = () => {
  let [current, setCurrent] = useState(1);

  const handleSubmit = () => {
    if (current >= 1 && current <= bullet.current.length) {
      bullet.current[current - 1].classList.add("active");
      progressCheck.current[current - 1].classList.add("active");
      progressText.current[current - 1].classList.add("active");
      setCurrent(current + 1);
      setTimeout(function () {
        alert("Your Form Successfully Signed up");
      }, 800);
    } else {
      console.log("Invalid current value:", current);
    }
  };
  return (
    <div class="container">
      <header>Login Form</header>
      <div class="form-outer">
        <form action="#">
          <div class="page">
            <div class="title">Login Details:</div>
            <div class="field">
              <div class="label">Username</div>
              <input type="text" />
            </div>
            <div class="field">
              <div class="label">Password</div>
              <input type="password" />
            </div>
            <div class="field btns">
              <button onClick={handleSubmit} class="submit">
                Submit
              </button>
            </div>
          </div>
        </form>
        <div>
          Dont have an acount? <Link to="/signup">Login</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
