import * as React from "react";
// import List from "@mui/material/List";
// import ListItem from "@mui/material/ListItem";
// import ListItemButton from "@mui/material/ListItemButton";
// import ListItemText from "@mui/material/ListItemText";
// import ListItemAvatar from "@mui/material/ListItemAvatar";
// import Checkbox from "@mui/material/Checkbox";
// import Avatar from "@mui/material/Avatar";
import {
  List,
  ListItem,
  ListItemButton,
  ListItemAvatar,
  ListItemText,
  Checkbox,
  Input,
  Container,
  Avatar,
  Box,
  Button,
} from "@mui/material";

const ShoppingList = ({ selectedProducts }) => {
  const [inputValues, setInputValues] = useState({
    input1: '',
    input2: '',
    input3: '',
  });

  const [products, setSelectedProducts] = useState([]); // State for selected products
  const checked = []; // Assuming you have an array of checked indices

  const handleInputChange = (inputName) => (event) => {
    setInputValues((prevValues) => ({
      ...prevValues,
      [inputName]: event.target.value,
    }));
  };

  const handleAddToList = () => {
    const newProduct = {
      id: selectedProducts.length + 1, // You can adjust the ID logic as needed
      description: inputValues.input1, // Use one of the inputs as the description
      images: [], // Add image data if available
    };

    setSelectedProducts((prevProducts) => [...prevProducts, newProduct]);
    setInputValues({
      input1: '',
      input2: '',
      input3: '',
    });
  };

  const handleToggle = (index) => () => {
    // Your toggle logic here
  };

  return (
    <div>
      <AppBar position="static">
        <Toolbar>
          {/* Your navbar content here */}
        </Toolbar>
      </AppBar>
      <Container>
        <Box display="flex" justifyContent="space-between" alignItems="center" marginTop={2}>
          <Input
            placeholder="Input 1"
            fullWidth
            value={inputValues.input1}
            onChange={handleInputChange('input1')}
          />
          <Input
            placeholder="Input 2"
            fullWidth
            value={inputValues.input2}
            onChange={handleInputChange('input2')}
          />
          <Input
            placeholder="Input 3"
            fullWidth
            value={inputValues.input3}
            onChange={handleInputChange('input3')}
          />
        </Box>
        <Button variant="contained" color="primary" onClick={handleAddToList}>
          Add to List
        </Button>
        <List dense sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper', marginTop: 2 }}>
          {selectedProducts.map((product, index) => {
            const labelId = `checkbox-list-secondary-label-${index}`;
            return (
              <ListItem
                key={product.id}
                secondaryAction={
                  <Checkbox
                    edge="end"
                    onChange={handleToggle(index)}
                    checked={checked.indexOf(index) !== -1}
                    inputProps={{ 'aria-labelledby': labelId }}
                  />
                }
                disablePadding
              >
                <ListItemButton>
                  <ListItemAvatar>
                    {/* Assuming you have imported Avatar */}
                    <Avatar alt={product.description} src={product.images[0]} sx={{ width: 50, height: 50 }} />
                  </ListItemAvatar>
                  <ListItemText id={labelId} primary={product.description} />
                </ListItemButton>
              </ListItem>
            );
          })}
        </List>
      </Container>
    </div>
  );
};

export default ShoppingList;
