import React, { useEffect, useState } from "react";
import "../index.css";
import axios from "axios";
import { Dropdown, Toast } from "react-bootstrap";

const ShoppingList = ({ sendProduct, setsendProduct }) => {
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [images, setImages] = useState("");
  let [products, setProducts] = useState([]);
  const token = localStorage.getItem("token");

  const showProducts = async () => {
    try {
      const userData = await axios.get("http://localhost:3001/users/myInfo", {
        headers: {
          "x-api-key": token,
        },
      });

      const { data } = await axios.get("http://localhost:3001/product");
      const userProducts = data.filter(
        (product) => product.user_id === userData.data._id
      );
      setProducts(userProducts);
    } catch (error) {
      console.log(error);
    }
  };

  const handleValues = async (e) => {
    e.preventDefault();
    const newProduct = {
      title: title,
      price: price,
      images: [images],
    };
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/product",
        newProduct,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      const data = response.data;
      setProducts([...products, data]);
      console.log([...products, data]);
    } catch (error) {
      console.log(error);
    }
    setsendProduct([...sendProduct, newProduct]);
    console.log([...sendProduct, newProduct]);
    setTitle("");
    setPrice("");
    setImages("");
  };

  const deleteProduct = async (product) => {
    try {
      console.log(product);
      const { data } = await axios.delete(
        `http://localhost:3001/product/${product._id}`,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const editProduct = async (product) => {
    try {
      console.log(product);
      const { data } = await axios.put(
        `http://localhost:3001/product/${product._id}`,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    showProducts();
  }, []);

  return (
    <div>
      <form onSubmit={handleValues}>
        <input className="text_input"
          placeholder="title of the product"
          onChange={(e) => setTitle(e.target.value)}
          name="title"
          required
        />
        <input className="text_input"
          placeholder="price"
          onChange={(e) => setPrice(e.target.value)}
          name="price"
          required
        />
        <input className="text_input"
          placeholder="images (url)"
          onChange={(e) => setImages(e.target.value)}
          name="img_url"
          required
        />
        <button type="submit">submit</button>
      </form>
      {products.map((product, index) => {
        return (
          <>
            <Toast key={index} className="toast">
              <Toast.Header>
                <img
                  style={{ width: "50px" }}
                  src={product.images[0]}
                  className="rounded me-2"
                  alt={product.description}
                />
                <strong style={{marginLeft:"10px"}} className="me-auto">{product.title}</strong>
                <small
                  style={{
                    marginLeft: "10px",
                    color: "green",
                    fontWeight: "bold",
                  }}
                >
                  {product.price}$
                </small>
              {/* <Dropdown>
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                  dropdown
                </Dropdown.Toggle>

                <Dropdown.Menu>
                  <Dropdown.Item>
                    <button
                      className="delete_product"
                      onClick={() => deleteProduct(product)}
                    >
                      {" "}
                      delete{" "}
                    </button>
                  </Dropdown.Item>
                  <Dropdown.Item>
                    {" "}
                    <button
                      className="edit_product"
                      onClick={() => editProduct(product)}
                    >
                      edit
                    </button>
                  </Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown> */}
              </Toast.Header>
              <Toast.Body>{product.description}</Toast.Body>
            </Toast>
          </>
        );
      })}
    </div>
  );
};

export default ShoppingList;
