import React, { useEffect, useState } from "react";
import {
  List,
  ListItem,
  ListItemButton,
  ListItemAvatar,
  ListItemText,
  Checkbox,
  Input,
  Container,
  Avatar,
  Box,
  Button,
  Menu,
  MenuItem,
} from "@mui/material";
import "../index.css";
import axios from "axios";

const ShoppingList = ({ sendProduct, setsendProduct }) => {
  const [checked, setChecked] = useState([]);
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [images, setImages] = useState("");
  let [products, setProducts] = useState([]);
  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const showProducts = async () => {
    try {
      const { data } = await axios.get("http://localhost:3001/product");
      setProducts(data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleValues = async (e) => {
    e.preventDefault();
    const newProduct = {
      title: title,
      price: price,
      images: [images],
    };
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/product",
        newProduct,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      const data = response.data;
      setProducts([...products, data]);
      console.log([...products, data]);
    } catch (error) {
      console.log(error);
    }
    setsendProduct([...sendProduct, newProduct]);
    console.log([...sendProduct, newProduct]);
    setTitle("");
    setPrice("");
    setImages("");
  };

  const deleteProduct = async () => {
    try {
      const { data } = await axios.delete(
        "http://localhost:3001/product",
        products._id
      );
      setProducts(data);
      console.log(data);
      setAnchorEl(null);
    } catch (error) {
      console.log(error);
    }
  };

  const editProduct = async () => {
    try {
      const { data } = await axios.put(
        "http://localhost:3001/product",
        products._id
      );
      setProducts(data);
      console.log(data);
      setAnchorEl(null);
    } catch (error) {
      console.log(error);
    }
  };

  // const handleToggle = (value) => () => {
  //   const currentIndex = checked.indexOf(value);
  //   const newChecked = [...checked];

  //   if (currentIndex === -1) {
  //     newChecked.push(value);
  //   } else {
  //     newChecked.splice(currentIndex, 1);
  //   }

  //   setChecked(newChecked);
  // };

  useEffect(() => {
    showProducts();
  }, []);

  return (
    <div>
      <Container>
        <form onSubmit={handleValues}>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            marginTop={2}
          >
            <Input
              placeholder="title of the product"
              sx={{ margin: 2 }}
              onChange={(e) => setTitle(e.target.value)}
              name="title"
              required
            />
            <Input
              placeholder="price"
              sx={{ margin: 2 }}
              onChange={(e) => setPrice(e.target.value)}
              name="price"
              required
            />
            <Input
              placeholder="images (url)"
              sx={{ margin: 2 }}
              onChange={(e) => setImages(e.target.value)}
              name="img_url"
              required
            />
            <Button type="submit">submit</Button>
          </Box>
        </form>
        <List
          dense
          sx={{
            width: "100%",
            maxWidth: 360,
            bgcolor: "background.paper",
            marginTop: 2,
          }}
        >
          {products.map((product, index) => {
            const labelId = `checkbox-list-secondary-label-${index}`;
            return (
              <ListItem
                key={index}
                secondaryAction={
                  // <Checkbox
                  //   edge="end"
                  //   onChange={handleToggle(index)}
                  //   checked={checked.indexOf(index) !== -1}
                  //   inputProps={{ "aria-labelledby": labelId }}
                  // />
                  <>
                    <Button
                      id="basic-button"
                      aria-controls={open ? "basic-menu" : undefined}
                      aria-haspopup="true"
                      aria-expanded={open ? "true" : undefined}
                      onClick={handleClick}
                    >
                      <i class="fa-solid fa-bars"></i>
                    </Button>
                    <Menu
                      id="basic-menu"
                      anchorEl={anchorEl}
                      open={open}
                      onClose={handleClose}
                      MenuListProps={{
                        "aria-labelledby": "basic-button",
                      }}
                    >
                      <MenuItem onClick={editProduct}>Edit</MenuItem>
                      <MenuItem onClick={deleteProduct}>Delete</MenuItem>
                    </Menu>
                  </>
                }
                disablePadding
              >
                <ListItemButton>
                  <ListItemAvatar>
                    <Avatar
                      alt={product.description}
                      src={product.images && product.images[0]}
                      sx={{ width: 50, height: 50 }}
                    />
                  </ListItemAvatar>
                  <ListItemText
                    id={labelId}
                    primary={`${product.price}$`}
                    secondary={product.title}
                  />
                </ListItemButton>
              </ListItem>
            );
          })}
        </List>
      </Container>
    </div>
  );
};

export default ShoppingList;
