import React, { useState } from "react";
import { Carousel } from "react-bootstrap";
import logo_img from "../Images/apple_logo.png";
import display from "../Images/apple_shop.png";
import 'bootstrap/dist/css/bootstrap.min.css';

const HeadSlide = () => {
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };

    return (
        <Carousel activeIndex={index} onSelect={handleSelect} data-bs-theme="dark">
      <Carousel.Item interval={1000}>
        <img className="d-block w-100" src={logo_img} alt="logo"/>
      </Carousel.Item>
      <Carousel.Item interval={1000}>
      <img className="d-block w-100" src={display} alt="display"/>
      </Carousel.Item>
    </Carousel>
    );
};

export default HeadSlide;
