import React, { useEffect, useState } from "react";
import "../index.css";
import axios from "axios";
import { Dropdown, Card, Button } from "react-bootstrap";

const ShoppingList = ({ sendProduct, setsendProduct }) => {
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [images, setImages] = useState("");
  let [products, setProducts] = useState([]);
  const token = localStorage.getItem("token");

  const showProducts = async () => {
    try {
      const userData = await axios.get("http://localhost:3001/users/myInfo", {
        headers: {
          "x-api-key": token,
        },
      });

      const { data } = await axios.get("http://localhost:3001/product");
      const userProducts = data.filter(
        (product) => product.user_id === userData.data._id
      );
      setProducts(userProducts);
    } catch (error) {
      console.log(error);
    }
  };

  const handleValues = async (e) => {
    e.preventDefault();
    const newProduct = {
      title: title,
      price: price,
      images: [images],
    };
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/product",
        newProduct,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      const data = response.data;
      setProducts([...products, data]);
      console.log([...products, data]);
    } catch (error) {
      console.log(error);
    }
    setsendProduct([...sendProduct, newProduct]);
    console.log([...sendProduct, newProduct]);
    setTitle("");
    setPrice("");
    setImages("");
  };

  const deleteProduct = async (product) => {
    try {
      console.log(product);
      const { data } = await axios.delete(
        `http://localhost:3001/product/${product._id}`,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const editProduct = async (product) => {
    try {
      console.log(product);
      const { data } = await axios.put(
        `http://localhost:3001/product/${product._id}`,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    showProducts();
  }, []);

  return (
    <div>
      <form onSubmit={handleValues}>
        <input
          className="text_input"
          placeholder="title of the product"
          onChange={(e) => setTitle(e.target.value)}
          name="title"
          required
        />
        <input
          className="text_input"
          placeholder="price"
          onChange={(e) => setPrice(e.target.value)}
          name="price"
          required
        />
        <input
          className="text_input"
          placeholder="images (url)"
          onChange={(e) => setImages(e.target.value)}
          name="img_url"
          required
        />
        <button className="text_button" type="submit">
          submit
        </button>
      </form>
      {products.map((product) => {
        return (
          <div key={product._id}>
            <Card className="text_card" style={{ width: "18rem" }}>
              <div className="card_container">
                <Card.Img
                  width={"100px"}
                  variant="top"
                  src={product.images[0]}
                />
                <Card.Body>
                  <Card.Title style={{ textAlign: "center" }}>
                    {product.title}
                  </Card.Title>
                  <Card.Text style={{ textAlign: "center" }}>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                  <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                      <i className="fa-solid fa-bars icon"></i>
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item>
                        <Button
                          className="delete_product"
                          onClick={() => deleteProduct(product)}
                        >
                          {" "}
                          delete{" "}
                        </Button>
                      </Dropdown.Item>
                      <Dropdown.Item>
                        {" "}
                        <Button
                          className="edit_product"
                          onClick={() => editProduct(product)}
                        >
                          edit
                        </Button>
                      </Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                </Card.Body>
              </div>
            </Card>
          </div>
        );
      })}
    </div>
  );
};

export default ShoppingList;
