import React, { useState } from "react";
import { Carousel, Button } from "react-bootstrap";
import logo_img from "../Images/apple_logo.png";
import display from "../Images/apple_shop.png";
import 'bootstrap/dist/css/bootstrap.min.css';

const MainBody = () => {
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };

    return (
        <Carousel activeIndex={index} onSelect={handleSelect}>
            <Carousel.Item>
                <img className="d-block w-100" src={logo_img} alt="logo"/>
                <Carousel.Caption>
                    <h3>Apple Logo</h3>
                </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
                <img className="d-block w-100" src={display} alt="display"/>
                <Carousel.Caption>
                    <h3>Apple Shop Display</h3>
                </Carousel.Caption>
            </Carousel.Item>
            {/* Carousel Control Buttons */}
            <div className="carousel-controls">
                <Button variant="prev" onClick={() => setIndex(index - 1)} disabled={index === 0}>
                    Previous
                </Button>
                <Button variant="next" onClick={() => setIndex(index + 1)} disabled={index === 1}>
                    Next
                </Button>
            </div>
        </Carousel>
    );
};

export default MainBody;
