import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Avatar from "@mui/material/Avatar";
import axios from "axios";

const Navbar = () => {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [user , setUser] = useState({});

  useEffect(() => {
    setToken(localStorage.getItem("token"));
  }, []);

  const getNameOfUser = async () => {
    // try {
      const data = await axios.get("http://localhost:3001/users/myInfo", {
        headers: {
          "x-api-key": token,
        },
      });
      setUser(data)
    // } catch (error) {
    //   console.log(error);
    // }
  };

  const handleLogout = () => {
    localStorage.clear();
    window.location.reload();
  };

  useEffect(() => {
    getNameOfUser();
  }, [])

  return (
    <div>
      <nav className="navbar">
        <h1>Super Shop</h1>
        <div className="links" style={{ display: "flex" }}>
          <Link to="/">Home</Link>
          {!token && (
            <>
              <Link to="/login">Login</Link>
              <Link to="/signup">Signup</Link>
            </>
          )}
          {token && (
            <div style={{ display: "flex" }}>
              <Link to="/login" onClick={handleLogout}>
                Logout
              </Link>
              <Link to="/list">Shopping List</Link>
              <Avatar sx={{ width: 32, height: 32, marginLeft: 5 }}>{user.name}</Avatar>
            </div>
          )}
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
