// import List from "@mui/material/List";
// import ListItem from "@mui/material/ListItem";
// import ListItemButton from "@mui/material/ListItemButton";
// import ListItemText from "@mui/material/ListItemText";
// import ListItemAvatar from "@mui/material/ListItemAvatar";
// import Checkbox from "@mui/material/Checkbox";
// import Avatar from "@mui/material/Avatar";
import React, { useState, useRef } from "react";
import {
  List,
  ListItem,
  ListItemButton,
  ListItemAvatar,
  ListItemText,
  Checkbox,
  Input,
  Container,
  Avatar,
  Box,
  Button,
} from "@mui/material";
import "../index.css";
import axios from "axios";

const ShoppingList = ({ sendProduct, setsendProduct }) => {
  const [checked, setChecked] = useState([]);
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [images, setImages] = useState("");
  const [products, setProducts] = useState("");
  const titleRef = useRef(null);
  const priceRef = useRef(null);
  const imagesRef = useRef(null);
  let newProduct = "";

  // const addProduct = async () => {
  //   const data = await axios
  //     .post("http://localhost:3001/product", products)
  //     .then((res) => setProducts(res.data))
  //     .catch((error) => console.log(error));
  //     console.log(data);
  // };

  const handleValues = async (e) => {
    e.preventDefault();
    newProduct = {
      title: titleRef.current.value,
      price: priceRef.current.value,
      images: [imagesRef.current.value],
    };
    setsendProduct(newProduct);
    setTitle("");
    setPrice("");
    setImages("");
  };

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  return (
    <div>
      <Container>
        <form onSubmit={handleValues}>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            marginTop={2}
          >
            <Input
              placeholder="title of the product"
              sx={{ margin: 2 }}
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              inputRef={titleRef}
              name="title"
              required
            />
            <Input
              placeholder="price"
              sx={{ margin: 2 }}
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              inputRef={priceRef}
              name="price"
              required
            />
            <Input
              placeholder="images (url)"
              sx={{ margin: 2 }}
              value={images}
              onChange={(e) => setImages(e.target.value)}
              inputRef={imagesRef}
              name="img_url"
              required
            />
            <Button type="submit">submit</Button>
          </Box>
        </form>
        <List
          dense
          sx={{
            width: "100%",
            maxWidth: 360,
            bgcolor: "background.paper",
            marginTop: 2,
          }}
        >
          {sendProduct.map((product, index) => {
            const labelId = `checkbox-list-secondary-label-${index}`;
            return (
              <ListItem
                key={index}
                secondaryAction={
                  <Checkbox
                    edge="end"
                    onChange={handleToggle(index)}
                    checked={checked.indexOf(index) !== -1}
                    inputProps={{ "aria-labelledby": labelId }}
                  />
                }
                disablePadding
              >
                <ListItemButton>
                  <ListItemAvatar>
                    <Avatar
                      alt={product.description}
                      src={product.images && product.images[0]}
                      sx={{ width: 50, height: 50 }}
                    />
                  </ListItemAvatar>
                  <ListItemText
                    id={labelId}
                    primary={`${product.price}$`}
                    secondary={product.title}
                  />
                </ListItemButton>
              </ListItem>
            );
          })}
        </List>
      </Container>
    </div>
  );
};

export default ShoppingList;
