import React, { useState } from "react";
import "../style/login.css";

const Login = () => {
  const [current, setCurrent] = useState(1);

  const handleNext = () => {
    setCurrent(current + 1);
  };

  const handlePrev = () => {
    setCurrent(current - 1);
  };

  const handleSubmit = () => {
    alert("Your Form Successfully Signed up");
    window.location.reload(); // Reloading the page after form submission
  };

  return (
    <div className="container">
      <header>Signup Form</header>
      {/* ... */}
      <div className="form-outer">
        <form action="#">
          <div className="page slide-page" style={{ marginLeft: `-${(current - 1) * 25}%` }}>
            {/* ... */}
            <div className="field btns">
              {current > 1 && (
                <button className={`prev-${current} prev`} onClick={handlePrev}>
                  Previous
                </button>
              )}
              {current < 4 ? (
                <button className={`next-${current} next`} onClick={handleNext}>
                  Next
                </button>
              ) : (
                <button className="submit" onClick={handleSubmit}>
                  Submit
                </button>
              )}
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
