import * as React from "react";
// import List from "@mui/material/List";
// import ListItem from "@mui/material/ListItem";
// import ListItemButton from "@mui/material/ListItemButton";
// import ListItemText from "@mui/material/ListItemText";
// import ListItemAvatar from "@mui/material/ListItemAvatar";
// import Checkbox from "@mui/material/Checkbox";
// import Avatar from "@mui/material/Avatar";
import {
  List,
  ListItem,
  ListItemButton,
  ListItemAvatar,
  ListItemText,
  Checkbox,
  Input,
  Container,
  Avatar,
  Box,
} from "@mui/material";

const ShoppingList = ({ selectedProducts }) => {
  const [checked, setChecked] = useState([]);
  const [value , setValue] = useState()

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  return (
    <div>
      <Container>
        <Box display="flex" justifyContent="space-between" alignItems="center" marginTop={2}>
          <Input placeholder="name of the product" fullWidth sx={{ margin: 2 }} />
          <Input placeholder="price" fullWidth sx={{ margin: 2 }} />
          <Input placeholder="image" fullWidth sx={{ margin: 2 }} />
        </Box>
        <List
          dense
          sx={{
            width: "100%",
            maxWidth: 360,
            bgcolor: "background.paper",
            marginTop: 2,
          }}
        >
          {selectedProducts.map((product, index) => {
            const labelId = `checkbox-list-secondary-label-${index}`;
            return (
              <ListItem
                key={product.id}
                secondaryAction={
                  <Checkbox
                    edge="end"
                    onChange={handleToggle(index)}
                    checked={checked.indexOf(index) !== -1}
                    inputProps={{ "aria-labelledby": labelId }}
                  />
                }
                disablePadding
              >
                <ListItemButton>
                  <ListItemAvatar>
                    <Avatar
                      alt={product.description}
                      src={product.images[0]}
                      sx={{ width: 50, height: 50 }}
                    />
                  </ListItemAvatar>
                  <ListItemText id={labelId} primary={product.description} />
                </ListItemButton>
              </ListItem>
            );
          })}
        </List>
      </Container>
    </div>
  );
};

export default ShoppingList;
