import React, { useEffect, useState } from "react";
import "../index.css";
import axios from "axios";
import { Card, Button } from "react-bootstrap";

const ShoppingList = ({ sendProduct, setsendProduct }) => {
  let [title, setTitle] = useState("");
  let [price, setPrice] = useState("");
  let [images, setImages] = useState("");
  const [amount, setAmount] = useState("");
  let [products, setProducts] = useState([]);
  const token = localStorage.getItem("token");

  const showProducts = async () => {
    try {
      const userData = await axios.get("http://localhost:3001/users/myInfo", {
        headers: {
          "x-api-key": token,
        },
      });

      const { data } = await axios.get("http://localhost:3001/product");
      const userProducts = data.filter(
        (product) => product.user_id === userData.data._id
      );
      setProducts(userProducts);
    } catch (error) {
      console.log(error);
    }
  };

  const handleValues = async (e) => {
    e.preventDefault();
    const newProduct = {
      title: title,
      price: price,
      images: [images],
    };
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/product",
        newProduct,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      const data = response.data;
      setProducts([...products, data]);
      console.log([...products, data]);
    } catch (error) {
      console.log(error);
    }
    setsendProduct([...sendProduct, newProduct]);
    console.log([...sendProduct, newProduct]);
    setTitle("");
    setPrice("");
    setImages("");
  };

  const deleteProduct = async (product) => {
    try {
      console.log(product);
      const { data } = await axios.delete(
        `http://localhost:3001/product/${product._id}`,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const editProduct = async (product) => {
    const newProduct = {
      title: title,
      price: price,
      images: [images],
    };
    try {
      console.log(product);
      const { data } = await axios.put(
        `http://localhost:3001/product/${product._id}`,
        newProduct,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log([...products, data]);
      setProducts([...products, data]);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    showProducts();
  }, []);

  return (
    <div>
      <form onSubmit={handleValues}>
        <input
          id="title_input"
          className="text_input"
          placeholder="title of the product"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          name="title"
          required
        />
        <input
        id="price_input"
          className="text_input"
          placeholder="price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          name="price"
          required
        />
        <input
        id="images_input"
          className="text_input"
          placeholder="images (url)"
          value={images}
          onChange={(e) => setImages(e.target.value)}
          name="images"
          required
        />
        <button id="button_submit" className="text_button" type="submit">
          submit
        </button>
      </form>
      <div id="form"></div>
      {products.map((product) => {
        return (
          <div key={product._id}>
            <Card className="text_card" style={{ width: "18rem" }}>
              <div className="card_container">
                <Card.Img
                  width={"100px"}
                  variant="top"
                  src={product.images[0]}
                />
                <Card.Body>
                  <Card.Title style={{ textAlign: "center" }}>
                    {product.title}
                  </Card.Title>
                  <Card.Text
                    style={{
                      textAlign: "center",
                      color: "green",
                      fontWeight: "bold",
                    }}
                  >
                    {product.price}$
                  </Card.Text>
                  <Card.Text style={{ textAlign: "center" }}>
                    {product.description}
                  </Card.Text>
                  <Button
                    className="delete_product"
                    onClick={() => deleteProduct(product)}
                  >
                    delete
                  </Button>
                  <Button
                    className="edit_product"
                    onClick={() => {
                      title = setTitle(product.title);
                      price = setPrice(product.price);
                      images = setImages(product.images[0]);
                      const submitButton = document.createElement("button");
                      submitButton.innerText = "Submit Button";
                      submitButton.addEventListener("click", (e) => {
                        console.log("editing");
                        const newProduct = {
                          title: setTitle(e.target.value),
                          price: setPrice(e.target.value),
                          images: setImages(e.target.value),
                        };
                        // console.log(...products, newProduct);
                        editProduct(newProduct);
                      });
                      const button = document.getElementById("button_submit");
                      const form = document.getElementById("form");
                      form.appendChild(submitButton);
                      button.remove();
                    }}
                  >
                    edit
                  </Button>
                  <input
                    placeholder="0"
                    onChange={(e) => setAmount(e.target.value)}
                    className="number_input"
                    type="number"
                  />
                </Card.Body>
              </div>
            </Card>
          </div>
        );
      })}
    </div>
  );
};

export default ShoppingList;
