import React, { useEffect, useState } from "react";
import "../index.css";
import axios from "axios";
import { Dropdown, Toast } from "react-bootstrap";

const ShoppingList = ({ sendProduct, setsendProduct }) => {
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [images, setImages] = useState("");
  let [products, setProducts] = useState([]);
  const token = localStorage.getItem("token");

  const showProducts = async () => {
    try {
      const userData = await axios.get("http://localhost:3001/users/myInfo", {
        headers: {
          "x-api-key": token,
        },
      });

      const { data } = await axios.get("http://localhost:3001/product");
      const userProducts = data.filter(
        (product) => product.user_id === userData.data._id
      );
      setProducts(userProducts);
    } catch (error) {
      console.log(error);
    }
  };

  const handleValues = async (e) => {
    e.preventDefault();
    const newProduct = {
      title: title,
      price: price,
      images: [images],
    };
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/product",
        newProduct,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      const data = response.data;
      setProducts([...products, data]);
      console.log([...products, data]);
    } catch (error) {
      console.log(error);
    }
    setsendProduct([...sendProduct, newProduct]);
    console.log([...sendProduct, newProduct]);
    setTitle("");
    setPrice("");
    setImages("");
  };

  const deleteProduct = async (product) => {
    try {
      console.log(product);
      const { data } = await axios.delete(
        `http://localhost:3001/product/${product._id}`,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const editProduct = async (product) => {
    try {
      console.log(product);
      const { data } = await axios.put(
        `http://localhost:3001/product/${product._id}`,
        {
          headers: {
            "x-api-key": token,
          },
        }
      );
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    showProducts();
  }, []);

  return (
    <div>
      <form onSubmit={handleValues}>
        <input
          placeholder="title of the product"
          onChange={(e) => setTitle(e.target.value)}
          name="title"
          required
        />
        <input
          placeholder="price"
          onChange={(e) => setPrice(e.target.value)}
          name="price"
          required
        />
        <input
          placeholder="images (url)"
          onChange={(e) => setImages(e.target.value)}
          name="img_url"
          required
        />
        <button type="submit">submit</button>
      </form>
      {products.map((product, index) => {
        return (
          <>
            <Toast>
              <Toast.Header>
                <img width={"200px"}
                  src={product.images[0]}
                  className="rounded me-2"
                  alt={product.description}
                />
                <strong className="me-auto">Bootstrap</strong>
                <small>11 mins ago</small>
              </Toast.Header>
              <Toast.Body>Hello, world! This is a toast message.</Toast.Body>
            </Toast>
            {/* <Dropdown>
              <Dropdown.Toggle variant="success" id="dropdown-basic">
                dropdown
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <Dropdown.Item>
                  <button
                    className="delete_product"
                    onClick={() => deleteProduct(product)}
                  >
                    {" "}
                    delete{" "}
                  </button>
                </Dropdown.Item>
                <Dropdown.Item>
                  {" "}
                  <button
                    className="edit_product"
                    onClick={() => editProduct(product)}
                  >
                    edit
                  </button>
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown> */}
          </>
        );
      })}
    </div>
  );
};

export default ShoppingList;
