import * as React from "react";
// import List from "@mui/material/List";
// import ListItem from "@mui/material/ListItem";
// import ListItemButton from "@mui/material/ListItemButton";
// import ListItemText from "@mui/material/ListItemText";
// import ListItemAvatar from "@mui/material/ListItemAvatar";
// import Checkbox from "@mui/material/Checkbox";
// import Avatar from "@mui/material/Avatar";
import {
  List,
  ListItem,
  ListItemButton,
  ListItemAvatar,
  ListItemText,
  Checkbox,
  Input,
  Container,
  Avatar,
  Box,
  Button,
} from "@mui/material";

const ShoppingList = ({ selectedProducts }) => {
  const [value, setValues] = React.useState({
    input1: '',
    input2: '',
    input3: '',
  });

  const [products, setProducts] = React.useState([]); // State for selected products
  const checked = []; // Assuming you have an array of checked indices

  const handlValues = (inputName) => (event) => {
    setValues((prevValues) => ({
      ...prevValues,
      [inputName]: event.target.value,
    }));
  };

  const handleAddToList = () => {
    const newProduct = {
      id: setProducts.length + 1, // You can adjust the ID logic as needed
      description: value.input1, // Use one of the inputs as the description
      images: [], // Add image data if available
    };

    setProducts((prevProducts) => [...prevProducts, newProduct]);
    setValues({
      input1: '',
      input2: '',
      input3: '',
    });
  };

  const handleToggle = (index) => () => {
    // Your toggle logic here
  };


  return (
    <div>
      <Container>
        <Box
          display="flex"
          justifyContent="space-between"
          alignItems="center"
          marginTop={2}
        >
          <Input
            value={value.name}
            onChange={handlValues("name")}
            placeholder="name of the product"
            fullWidth
            sx={{ margin: 2 }}
          />
          <Input
            type="number"
            value={value.price}
            onChange={handlValues("price")}
            placeholder="price"
            fullWidth
            sx={{ margin: 2 }}
          />
          <Input
            value={value.image}
            onChange={handlValues("image")}
            placeholder="image"
            fullWidth
            sx={{ margin: 2 }}
          />
          <Button>submit</Button>
        </Box>
        <List
          dense
          sx={{
            width: "100%",
            maxWidth: 360,
            bgcolor: "background.paper",
            marginTop: 2,
          }}
        >
          {selectedProducts.map((product, index) => {
            const labelId = `checkbox-list-secondary-label-${index}`;
            return (
              <ListItem
                key={product.id}
                secondaryAction={
                  <Checkbox
                    edge="end"
                    onChange={handleToggle(index)}
                    checked={checked.indexOf(index) !== -1}
                    inputProps={{ "aria-labelledby": labelId }}
                  />
                }
                disablePadding
              >
                <ListItemButton>
                  <ListItemAvatar>
                    <Avatar
                      alt={product.description}
                      src={product.images[0]}
                      sx={{ width: 50, height: 50 }}
                    />
                  </ListItemAvatar>
                  <ListItemText id={labelId} primary={product.description} />
                </ListItemButton>
              </ListItem>
            );
          })}
        </List>
      </Container>
    </div>
  );
};

export default ShoppingList;
